
#ifndef __DOGINTERGER_H_
#define __DOGINTERGER_H_

    /** 无符号8位
    */
    using u8 = unsigned char;

    /** 无符号16位
    */
    using u16 = unsigned short;

    /** 无符号32位
    */
    using u32 = unsigned long;

    /** 无符号64位
    */
    using u64 = unsigned long long;

    /** 有符号8位
    */
    using s8 = char;

    /** 有符号16位
    */
    using s16 = short;

    /** 有符号32位
    */
    using s32 = long;

    /** 有符号64位
    */
    using s64= long long;

    /** 32位有符号数最大值
    */
    #define  MAX_S32 0x7fffffff

    /** 32位无符号数最大值
    */
    #define  MAX_U32 0xffffffff

#endif // __DOGINTERGER_H_
